#!/bin/bash
# Stats (TXT)
# Arguments: 1 (source .webm file), 2 (destination folder)

INSERT INTO encodes (id, episode, preset, videoBitrate, audioBitrate) VALUES (\"$entry-$item-8\", \"$entry-$item\", 8, $(ffprobe -i "/mnt/raid/nyananime-xyz-video/$entry/$item/ep_vp.webm" -v quiet -select_streams v:0 -show_entries stream -hide_banner -of default=noprint_wrappers=1 | grep TAG:NUMBER_OF_BYTES | sed "s/TAG:NUMBER_OF_BYTES-eng=//"), $(ffprobe -i "/mnt/raid/nyananime-xyz-video/$entry/$item/ep_vp.webm" -v quiet -select_streams a:0 -show_entries stream -hide_banner -of default=noprint_wrappers=1 | grep TAG:NUMBER_OF_BYTES | sed "s/TAG:NUMBER_OF_BYTES-eng=//"), $(stat --format "%s" "/mnt/raid/nyananime-xyz-video/$entry/$item/ep_vp.webm"), $(ffprobe -i "/mnt/raid/nyananime-xyz-video/$entry/$item/ep_vp.webm" -v quiet -show_entries format=duration -hide_banner -of default=noprint_wrappers=1:nokey=1))\n